CREATE DEFINER=`angelo`@`localhost` PROCEDURE `ReopenLancto`(
  IN p_Id INT
)
BEGIN
  IF NOT (SELECT 1 FROM `lancto` WHERE `Id` = p_Id) THEN
    SIGNAL SQLSTATE '45000'
    SET MESSAGE_TEXT = 'Lançamento não existe';
